angular.module("dealModule",["angularUtils.directives.dirPagination"]).controller("dealCtrl",["$scope",function(a){a.currentPage=1,a.pageSize=10,a.drinks=[];for(var e=["coke","melange","chai latte","almdudler","beer","vodka","coconut milk","orange juice","wine","whisky","sex on the beach"],i=1;i<=20;i++){var n=e[Math.floor(Math.random()*e.length)];a.drinks.push("drink "+i+": "+n)}a.pageChangeHandler=function(a){console.log("drinks page changed to "+a)},a.toggleSidebar=function(){"0px"==$("#sidebar-affix").css("right")?$("#sidebar-affix").animate({right:"-1000",display:"none"},500):$("#sidebar-affix").animate({right:"0",display:"block"},500)}}]);