angular.module("storeModule",["angularUtils.directives.dirPagination","storeServiceModule"]).controller("storeCtrl",["$scope","storeFactory",function(e,a){e.currentPage=1,e.pageSize=10,e.drinks=[];for(var r=["coke","melange","chai latte","almdudler","beer","vodka","coconut milk","orange juice","wine","whisky","sex on the beach"],i=1;i<=20;i++){var o=r[Math.floor(Math.random()*r.length)];e.drinks.push("drink "+i+": "+o)}e.pageChangeHandler=function(e){console.log("drinks page changed to "+e)},e.toggleSidebar=function(){"0px"==$("#sidebar-affix").css("right")?$("#sidebar-affix").animate({right:"-1000",display:"none"},500):$("#sidebar-affix").animate({right:"0",display:"block"},500)}}]);